            stubClassLoader::load($fqClassName);
        }

        $bindings = array();
        if (method_exists($nqClassName, '__bindings') === true) {
            $bindings = call_user_func_array(array($nqClassName, '__bindings'), array($projectPath));
        }

        if (null !== $argv) {
            $bindings[] = new stubArgumentsBindingModule($argv);
        }

