     * @param   string       $type
     * @param   string       $name
     * @return  stubBinding
     */
    protected function getBinding($type, $name = null)
    {
        $bindingIndex = $this->getIndex();
        if (null !== $name) {
            if (isset($bindingIndex[$type . '#' . $name]) === true) {
                return $bindingIndex[$type . '#' . $name];
            }
