     */
    const METHOD_HEAD  = 'HEAD';
    /**
     * HTTP version: 1.0
     */
    const VERSION_1_0  = 'HTTP/1.0';
    /**
     * HTTP version: 1.1
     */
    const VERSION_1_1  = 'HTTP/1.1';

