                                      LDAP_OPT_RESTART          => LDAP_OPT_RESTART,
                                      LDAP_OPT_HOST_NAME        => LDAP_OPT_HOST_NAME,
                                      LDAP_OPT_ERROR_STRING     => LDAP_OPT_ERROR_STRING,
                                      LDAP_OPT_MATCHED_DN       => LDAP_OPT_MATCHED_DN,
