        if (isset(self::$tableDescriptions[$fqClassName]) === false) {
            if ($entityClass->hasAnnotation('DBTable') === true) {
                 self::$tableDescriptions[$fqClassName] = $entityClass->getAnnotation('DBTable')->getTableDescription();
