        $fqClassName = $entityClass->getFullQualifiedClassName();
        if (isset(self::$columns[$fqClassName]) === false) {
            self::$columns[$fqClassName] = array();
            foreach ($entityClass->getMethods() as $method) {
                $column = $this->getTableColumn($method);
                if (null === $column) {
