        if (isset(self::$annotations[$target][$targetName]) == false) {
            return false;
        }
        
        if (isset(self::$annotations[$target][$targetName][$annotationName]) == false) {
            return false;
        }
        
        return self::$annotations[$target][$targetName][$annotationName] === '';
    }

