    protected function indexCalculator($prefixedNumber)
    {
        $padding  = self::PAD_SLOTS - strlen(self::$msgCount);
        $numIndex = $prefixedNumber . str_pad('', $padding, '0') . self::$msgCount;
        self::$msgCount++;
