        if (null === $month) {
            $month = (int) date('m');
            $day   = (int) date('d');
            // if today is first day of the month use previous month
            if (1 === $day) {
                // if month is January set $month to December of the last year, else decrease month
                if (1 === $month) {
                    $month = 12;
                    $year--;
