        try {
            $processor = $this->processorResolver->resolve($this->request, $this->session, $this->response);
            if ($processor->forceSsl() === true && $processor->isSsl() === false) {
                $this->response->addHeader('Location', 'https://' . $this->request->getURI());
                $this->request->cancel();
                $this->response->send();
